mapper.select:
   	 
   	 	select bno,title,content,writer,regdate
			from(
    			select /*+ INDEX_DESC(tbl_board pk_board) */ 
        		rownum rn, bno, title, content,writer,regdate
    		from tbl_board
    		where rownum <= #{page} *#{perPageNum} 
    		)
		where rn > (#{page}-1) *#{perPageNum}
